fn quicksort<T: Ord>(arr: &mut [T]) {
    if arr.len() <= 1 {
        return;
    }
    let pivot_index = partition(arr);
    let (left, right) = arr.split_at_mut(pivot_index);
    quicksort(left);
    quicksort(&mut right[1..]); // Exclude the pivot from the right side
}

fn partition<T: Ord>(arr: &mut [T]) -> usize {
    let len = arr.len();
    let pivot_index = len / 2; // Choose middle element as pivot
    arr.swap(pivot_index, len - 1); // Move pivot to end

    let mut i = 0;
    for j in 0..len - 1 {
        if arr[j] <= arr[len - 1] {
            arr.swap(i, j);
            i += 1;
        }
    }
    arr.swap(i, len - 1); // Move pivot to its final place
    i
}

fn main() {
    let mut numbers = vec![9, 4, 1, 7, 3, 8, 2, 6, 5];
   quicksort(&mut numbers);
    println!(" {:?}", numbers);


}
